var tocNav=document.getElementById("TableOfContents"),tocLinksLi=tocNav.getElementsByTagName("a");window.addEventListener("DOMContentLoaded",()=>{const e=new IntersectionObserver(e=>{e.forEach(e=>{const n=e.target.getAttribute("id");let t=tocNav.querySelector(`a[href="#${n}"]`).parentElement;e.intersectionRatio>0?(t.classList.add("toc-active"),previousNode=t):t.classList.remove("toc-active")})}),t=document.querySelectorAll("h1, h2, h3");t.forEach(t=>{e.observe(t)})})